INSERT INTO "public"."stories" ("id", "title", "description", "cover_image_url", "topic_id") VALUES
('4e525a78-2c6d-4731-9a37-2a62883398c1', 'Chuyến Phiêu Lưu Của Chai Nhựa Tí Hon', 'Một câu chuyện kể về hành trình kỳ diệu của một chai nhựa nhỏ bé, từ lúc bị bỏ đi cho đến khi được tái chế và bắt đầu một cuộc sống mới đầy ý nghĩa.', 'https://sggniqcffaupphqfevrp.supabase.co/storage/v1/object/public/asset//ChatGPT%20Image%2022_10_30%2016%20thg%207,%202025.png', '5551cafe-f7d8-4930-88e8-fcf820dcb5bd');
INSERT INTO "public"."story_segments" ("id", "story_id", "segment_index", "vi_text", "image_url") VALUES
(uuid_generate_v4(), '4e525a78-2c6d-4731-9a37-2a62883398c1', 1, 'Tại một góc nhỏ trên bệ cửa sổ đầy nắng, có một bạn Chai Nhựa tên là Tí Hon. Tí Hon rất vui vì được chứa đầy nước mát và ngắm nhìn thế giới bên ngoài qua lớp nhựa trong veo của mình.', 'https://sggniqcffaupphqfevrp.supabase.co/storage/v1/object/public/asset//ChatGPT%20Image%2022_10_40%2016%20thg%207,%202025.png'),
(uuid_generate_v4(), '4e525a78-2c6d-4731-9a37-2a62883398c1', 2, 'Một ngày nọ, sau khi đã hết nước, Tí Hon bị bỏ vào một thùng rác tối om cùng với những đồ vật khác. Tí Hon cảm thấy rất buồn và cô đơn. "Chẳng lẽ cuộc đời mình kết thúc ở đây sao?", bạn ấy tự hỏi.', 'https://sggniqcffaupphqfevrp.supabase.co/storage/v1/object/public/asset//ChatGPT%20Image%2022_10_43%2016%20thg%207,%202025.png'),
(uuid_generate_v4(), '4e525a78-2c6d-4731-9a37-2a62883398c1', 3, 'Bỗng nhiên, một bàn tay nhỏ xinh của cô bé tên An đã nhặt Tí Hon ra. An nói: "Bạn không phải là rác đâu! Bạn có thể bắt đầu một hành trình mới!" rồi nhẹ nhàng đặt Tí Hon vào một chiếc thùng màu vàng có biểu tượng những mũi tên xoay tròn.', 'https://sggniqcffaupphqfevrp.supabase.co/storage/v1/object/public/asset//ChatGPT%20Image%2022_10_47%2016%20thg%207,%202025.png'),
(uuid_generate_v4(), '4e525a78-2c6d-4731-9a37-2a62883398c1', 4, 'Trong thùng mới, Tí Hon gặp Vỏ Lon Sành Điệu và Hộp Giấy Thông Thái. Chẳng bao lâu, một chú Xe Tái Chế to lớn, thân thiện đến và cười nói: "Xin chào các bạn nhỏ! Chúng ta cùng đến Nhà Máy Diệu Kỳ để biến hình nào!"', 'https://sggniqcffaupphqfevrp.supabase.co/storage/v1/object/public/asset//ChatGPT%20Image%2022_10_50%2016%20thg%207,%202025.png'),
(uuid_generate_v4(), '4e525a78-2c6d-4731-9a37-2a62883398c1', 5, 'Tại Nhà Máy Diệu Kỳ, Tí Hon cảm thấy mình xoay tít rồi biến đổi. Khi mở mắt ra, Tí Hon đã trở thành một chiếc bình tưới cây màu đỏ thắm, xinh xắn. Vỏ Lon thì thành một chiếc xe đạp mới, còn Hộp Giấy thì biến thành một quyển truyện tranh.', 'https://sggniqcffaupphqfevrp.supabase.co/storage/v1/object/public/asset//ChatGPT%20Image%2022_10_54%2016%20thg%207,%202025.png'),
(uuid_generate_v4(), '4e525a78-2c6d-4731-9a37-2a62883398c1', 6, 'Giờ đây, Tí Hon mỗi ngày đều được cùng bé An tưới cho những bông hoa rực rỡ trong vườn. Tí Hon hiểu rằng, được tái chế là một chuyến phiêu lưu tuyệt vời, giúp những món đồ cũ có một cuộc sống mới thật ý nghĩa và hạnh phúc.', 'https://sggniqcffaupphqfevrp.supabase.co/storage/v1/object/public/asset//ChatGPT%20Image%2022_11_53%2016%20thg%207,%202025.png');